const newWidget = () => {
  const content = new Widget();
  const htmlUrl = '/bearborg/index.html';  // Serve from server extension

  fetch(htmlUrl)
    .then(response => response.text())
    .then(html => {
      content.node.innerHTML = html;
    });

  content.node.style.overflow = 'auto';
  content.node.style.height = '100%';
  content.addClass('jp-About-content');

  const widget = new MainAreaWidget({ content });
